    private void docentePrActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_docentePrActionPerformed
        // TODO add your handling code here:
        String Usuario = usuario.getText();
        String Password = password.getText();
        if (Usuario.isEmpty() || Password.isEmpty()) {
            JOptionPane.showMessageDialog(null, "Llene el campo");
        } else {
            if (Usuario.equals("Admin") && Password.equals("E$p3.2023")) {
                Laboratorio01 docenteP = new Laboratorio01();
                docenteP.setVisible(true);
                this.setVisible(false);
            }else{
                JOptionPane.showMessageDialog(null, "Usuario o contrase√±a incorrecta");
            }
        }
    }//GEN-LAST:event_docentePrActionPerformed

